/**
 * This method is used to check internet connection available or not
 * @param context context of application
 * @return status of network connection
 */
fun isConnectedToInternet(context: Context): Boolean {
    val connectivityManager =
        context.getSystemService(Context.CONNECTIVITY_SERVICE) as ConnectivityManager
    if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.Q) {
        val capabilities =
            connectivityManager.getNetworkCapabilities(connectivityManager.activeNetwork)
        if (capabilities != null) {
            when {
                capabilities.hasTransport(NetworkCapabilities.TRANSPORT_CELLULAR) -> {
                    return true
                }
                capabilities.hasTransport(NetworkCapabilities.TRANSPORT_WIFI) -> {
                    return true
                }
                capabilities.hasTransport(NetworkCapabilities.TRANSPORT_ETHERNET) -> {
                    return true
                }
            }
        }
    } else {
        val activeNetworkInfo = connectivityManager.activeNetworkInfo
        if (activeNetworkInfo != null && activeNetworkInfo.isConnected) {
            return true
        }
    }
    return false
}

/**
 * This method is used to parse network error
 * @param e exception
 * @return error code with error message
 */
fun geNetworkError(e: Exception): OnError {
    when (e) {
        is SocketTimeoutException -> {
            return OnError(ERROR_CODE_401, ERROR_MSG_401)
        }
        is UnknownHostException -> {
            return OnError(ERROR_CODE_500, ERROR_MSG_500)
        }
        is ConnectException -> {
            return OnError(ERROR_CODE_402, ERROR_MSG_402)
        }
        is IOException -> {
            return OnError(ERROR_CODE_403, ERROR_MSG_403)
        }
        else -> {
            return OnError(ERROR_CODE_400, ERROR_MSG_400)
        }
    }
}

/**
 * This method is used to return color code based on status
 * @param status status of character
 * @return color code
 */
fun getColorCode(status: String?, context: Context): Int {
    return when {
        status.equals(ALIVE, true) -> {
            context.getColor(R.color.alive)
        }
        status.equals(DEAD, true) -> {
            context.getColor(R.color.dead)
        }
        status.equals(UNKNOWN, true) -> {
            context.getColor(R.color.unknown)
        }
        else -> {
            context.getColor(R.color.black)
        }
    }
}

/**
 * This method is used to image height as per ratio
 * @param context context of application
 * @return height of imageview
 */
fun getImageHeight(context: Context): Int {
    val displayMetrics = context.resources.displayMetrics
    return ((displayMetrics.widthPixels * IMAGE_HEIGHT_RATIO) / IMAGE_WIDTH_RATIO)
}

